CREATE TABLE EGGMAN ( ID INT , NAME VARCHAR ( 100 ) , SEX CHAR ( 1 ) ) INSERT INTO EGGMAN ( ID , NAME , SEX ) SELECT 1, 'Mark' , 'M' UNION SELECT 2 , 'Matthew' , 'M' UNION SELECT 3 , 'Luke' , 'M' UNION SELECT 4 , 'John' , 'M' UNION SELECT 5 , 'Phillip' , 'M' UNION SELECT 6 , 'Edith' , 'F' UNION SELECT 7 , 'Roxanne' , 'F' UNION SELECT 8 , 'Rochelle' , 'F' UNION SELECT 9 , 'Diane', 'F' UNION SELECT 10 , 'Alexa' , 'F' DECLARE @REQSEX CHAR(1) SELECT @REQSEX = 'M' SELECT NAME FROM EGGMAN WHERE ID = ( CAST ( RAND ( ) * 100 AS INT ) % 5 ) + CASE WHEN @REQSEX = 'M' THEN 1 ELSE 6 END 
